class'xLineAutomation'function xLineAutomation:__init(a)self.automation=table.create()if a then for b,c in ipairs(a)do self.automation:insert(c)end end end;function xLineAutomation:do_write(d,e,f)TRACE("xLineAutomation:do_write(line,ptrack_auto,patt_num_lines)",d,e,f)assert(e,"Required argument 'ptrack_auto' not defined")if table.is_empty(self.automation)then return end;if renoise.API_VERSION<=5 then e:clear_range(d,d+1)end;for g,c in ipairs(self.automation)do e:add_point_at(d+c.time_offset or 0,c.value or 0)end;if d==f then e:add_point_at(d+0.999,self.automation[1].value)end end;function xLineAutomation:__tostring()return type(self)end